import React from "react";
import './Footer.css';

class Footer extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            name : "",
            number: ""
        }
        this.handleNameChange = this.handleNameChange.bind(this);
        this.handleNumberChange = this.handleNumberChange.bind(this);
        this.handleSave = this.handleSave.bind(this)
    }

    handleNameChange(event) {
        this.setState({ content: event.target.value })
    }
    render() {
    return(
        <footer>
            <input value={this.state.content} onChange={this.handleChange} placeholder="Unesite ime"></input>
            <input value={this.state.content} onChange={this.handleChange} placeholder="Unesite broj"></input>
            <button onClick={() => this.props.createItem(this.state.content)}>Sacuvaj</button>
        </footer>
    );
    }
}

export default Footer;
